#include<pthread.h>#include "WorkThread.h"void WorkThread::init_thread(const char* name, void func(void*), void* param,  const long stackSize){}void WorkThread::processEntry(void* param){    IThread* owner = static_cast<IThread*>(param);    owner->setThreadState(EBusy);    owner->dispatch();}void WorkThread::dispatch(){    loger_ << debug << "dispatch event......";    IEvent* ev = static_cast<IEvent*>(queue_->takeMessage());    if (NULL != ev)    {        ev->getDestination()->takeEvent(ev);    }}